examples.before:
// OrderService.java
public class OrderService {
    private CustomerService customerService; // depends on CustomerService
}

// CustomerService.java
public class CustomerService {
    private OrderService orderService; // circular: depends on OrderService
}

examples.after:
// Extract interface to break cycle
public interface OrderProvider {
    List<Order> getOrdersForCustomer(String customerId);
}

public class OrderService implements OrderProvider { }

public class CustomerService {
    private OrderProvider orderProvider; // depends on interface, not concrete class
}
